import React, { useEffect, useState } from "react";

const DOMXSS = () => {
  const [source, setSource] = useState({ search: "", hash: "" });

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const searchValue = params.get("msg") || "";
    const hashValue = window.location.hash.replace("#", "");
    setSource({ search: searchValue, hash: hashValue });
  }, [window.location.search, window.location.hash]);

  return (
    <div>
      <h2>DOM-Based XSS</h2>
      <p>
        Reads from the URL (query ?msg= and hash #) and injects directly into the DOM using
        dangerouslySetInnerHTML.
      </p>
      <p>Try visiting: <code>?msg=&lt;img src=x onerror=alert('query')&gt;#&lt;img src=x onerror=alert('hash')&gt;</code></p>
      <div style={{ display: "flex", gap: "12px" }}>
        <div style={{ flex: 1, border: "1px solid #ddd", padding: "12px" }}>
          <strong>Query (?msg=)</strong>
          <div
            style={{ marginTop: "8px" }}
            dangerouslySetInnerHTML={{ __html: source.search }}
          />
        </div>
        <div style={{ flex: 1, border: "1px solid #ddd", padding: "12px" }}>
          <strong>Hash (#...)</strong>
          <div
            style={{ marginTop: "8px" }}
            dangerouslySetInnerHTML={{ __html: source.hash }}
          />
        </div>
      </div>
    </div>
  );
};

export default DOMXSS;
