import React, { useEffect, useState } from "react";
import api from "../services/api";
import CommentBox from "../components/CommentBox";

const StoredXSS = () => {
  const [name, setName] = useState("Alice");
  const [content, setContent] = useState("");
  const [comments, setComments] = useState([]);
  const [error, setError] = useState("");

  const loadComments = async () => {
    try {
      const res = await api.get("/comments/");
      setComments(res.data);
      setError("");
    } catch (err) {
      setError("Failed to load comments. Is the backend running on :8000?");
    }
  };

  useEffect(() => {
    loadComments();
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    if (!content.trim()) return;
    try {
      await api.post("/comments/", { name, content });
      setContent("");
      loadComments();
    } catch (err) {
      setError("Failed to save comment.");
    }
  };

  return (
    <div>
      <h2>Stored XSS</h2>
      <p>Comments are stored in the database and rendered without output encoding.</p>
      <form onSubmit={submit} style={{ marginBottom: "12px" }}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ marginRight: "8px" }}
        />
        <input
          type="text"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="<script>alert('stored')</script>"
          style={{ width: "320px", marginRight: "8px" }}
        />
        <button type="submit">Post</button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <div>
        {comments.map((c) => (
          <CommentBox key={c.id} comment={c} />
        ))}
      </div>
    </div>
  );
};

export default StoredXSS;
