import React from "react";

const boxStyle = {
  border: "1px solid #ccc",
  borderRadius: "4px",
  padding: "12px",
  marginBottom: "8px",
};

const CommentBox = ({ comment }) => {
  return (
    <div style={boxStyle}>
      <strong>{comment.name}</strong>
      <div
        style={{ marginTop: "6px" }}
        dangerouslySetInnerHTML={{ __html: comment.content }}
      />
      <small style={{ color: "#666" }}>{new Date(comment.created).toLocaleString()}</small>
    </div>
  );
};

export default CommentBox;
