import React, { useState } from "react";
import api from "../services/api";

const ReflectedXSS = () => {
  const [query, setQuery] = useState("");
  const [resultHtml, setResultHtml] = useState("");
  const [error, setError] = useState("");

  const search = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const response = await api.get("/search/", { params: { q: query } });
      setResultHtml(response.data || response.request.responseText);
    } catch (err) {
      setError("Request failed. Is the backend running on :8000?");
    }
  };

  return (
    <div>
      <h2>Reflected XSS</h2>
      <p>Server reflects the query directly back without encoding.</p>
      <form onSubmit={search} style={{ marginBottom: "12px" }}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Type <script>alert(1)</script>"
          style={{ width: "320px", marginRight: "8px" }}
        />
        <button type="submit">Search</button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <div
        style={{ border: "1px solid #ddd", padding: "12px", minHeight: "60px" }}
        dangerouslySetInnerHTML={{ __html: resultHtml }}
      />
    </div>
  );
};

export default ReflectedXSS;
